AMY* amy_create()
{
    AMY* amy = (AMY*)malloc(sizeof(AMY));
    return amy;
}

void amy_reset(AMY* amy)
{
    memset((void*)amy, 0, sizeof(AMY));
    memcpy(amy->octave_splitter_rom, octave_splitter_rom_global, AMY_OCTAVE_SPLITTER_ROM_SIZE * sizeof(amy->octave_splitter_rom[0]));
    memcpy(amy->base_exp_rom, base_exp_rom_global, AMY_BASE_EXP_ROM_SIZE * sizeof(amy->base_exp_rom[0]));
    memcpy(amy->int_exp_rom, int_exp_rom_global, AMY_INTERPOLATION_EXP_ROM_SIZE * sizeof(amy->int_exp_rom[0]));
    memcpy(amy->base_sin_rom, base_sin_rom_global, AMY_BASE_SIN_ROM_SIZE * sizeof(amy->base_sin_rom[0]));
    memcpy(amy->int_sin_rom, int_sin_rom_global, AMY_INTERPOLATION_SIN_ROM_SIZE * sizeof(amy->int_sin_rom[0]));
}

void amy_free(AMY* amy)
{
    free(amy);
}

void amy_write_reg_a(AMY* amy, uint8_t data)
{
    amy->reg_a = data;
}

void amy_write_reg_b(AMY* amy, uint8_t data)
{
    amy->reg_b = data;
}

void amy_write_reg_c(AMY* amy, uint8_t data)
{
    amy->reg_c = data;
}

void amy_write_reg_command(AMY* amy, uint8_t data)
{
    if((data & AMY_FREQ_ENV_BREAKPOINT_CMD_MASK) == AMY_FREQ_ENV_BREAKPOINT_CMD_CODE)
    {
        //0 slope = immediate change
        if(amy->reg_a == 0)
        {
            amy->chan[data & 7].env.running = false;
            amy->chan[data & 7].env.destination = amy->reg_c | ((uint16_t)amy->reg_b << 8);
            amy->chan[data & 7].env.curr_val = ((uint32_t)amy->reg_c | ((uint32_t)amy->reg_b << 8)) << 5;
            return;
        }

        amy->chan[data & 7].env.slope = amy->reg_a;
        amy->chan[data & 7].env.destination = amy->reg_c | ((uint16_t)amy->reg_b << 8);
        amy->chan[data & 7].env.running = true;
        return;
    }
    if((data & AMY_VOICE_TYPE_CMD_MASK) == AMY_VOICE_TYPE_CMD_CODE)
    {
        amy->chan[data & 7].type = amy->reg_a & 3;
        return;
    }
    if((data & AMY_READ_CURR_FREQ_CMD_MASK) == AMY_READ_CURR_FREQ_CMD_CODE)
    {
        amy->reg_c = (amy->chan[data & 7].env.curr_val >> 5) & 0xff;
        amy->reg_b = (amy->chan[data & 7].env.curr_val >> (5 + 8)) & 0x1f;
        return;
    }
    if((data & AMY_SYSTEM_OPTIONS_CMD_MASK) == AMY_SYSTEM_OPTIONS_CMD_CODE)
    {
        amy->ale_mode = data & 1;
        amy->int_mode = data & 2;
        amy->individual_mode = data & 4;
        amy->forty_harm = data & 8;
        return;
    }
    if((data & AMY_CONTROL_CMD_MASK) == AMY_CONTROL_CMD_CODE)
    {
        amy->seq_reset = data & 1;

        if(!(amy->seq_reset))
        {
            //TODO: reset phase of all oscillators of selected voices???
        }

        amy->nzinit = data & 2;
        return;
    }
    if((data & AMY_VOL_ENV_BREAKPOINT_CMD_MASK) == AMY_VOL_ENV_BREAKPOINT_CMD_CODE)
    {
        //0 slope = immediate change
        if(amy->reg_a == 0)
        {
            amy->harm_osc[data & 63].env.running = false;
            amy->harm_osc[data & 63].env.destination = amy->reg_c;
            amy->harm_osc[data & 63].env.curr_val = (uint16_t)amy->reg_c << 5;
            return;
        }

        amy->harm_osc[data & 63].env.slope = amy->reg_a;
        amy->harm_osc[data & 63].env.destination = amy->reg_c;
        amy->harm_osc[data & 63].env.running = true;
        return;
    }
    if((data & AMY_HARM_PAIR_NOISE_RAM_CMD_MASK) == AMY_HARM_PAIR_NOISE_RAM_CMD_CODE)
    {
        if(amy->nzinit)
        {
            //TODO: figure out what the fuck is noise RAM actually!
            return;
        }
        else
        {
            //write last harmonic pair flag...
            amy->harmonic_pair_flags &= ~(1 << (((data >> 1)) & 0x1f));

            if(data & 1)
            {
                amy->harmonic_pair_flags |= (1 << (((data >> 1)) & 0x1f));
            }
            
            return;
        }
    }
    if((data & AMY_READ_CURR_VOL_ENV_CMD_MASK) == AMY_READ_CURR_VOL_ENV_CMD_CODE)
    {
        amy->reg_c = (amy->harm_osc[data & 63].env.curr_val >> 5) & 0xff;
        return;
    }
}

uint8_t amy_read_reg_a(AMY* amy)
{
    return amy->reg_a;
}

uint8_t amy_read_reg_b(AMY* amy)
{
    return amy->reg_b;
}

uint8_t amy_read_reg_c(AMY* amy)
{
    return amy->reg_c;
}

uint16_t amy_slope_to_step_vol(uint8_t slope)
{
    uint8_t slope_signed = ((slope & 0x1f) << 3); 
    
    return (((slope & 0x80) && !(slope & 0x10)) ? -1 : 1) * ((*(int8_t*)&slope_signed / 8)); /*WTF is this*/
}

uint16_t amy_slope_to_step_pitch(uint8_t slope)
{
    uint8_t slope_signed = ((slope & 0x1f) << 3); 
    
    return (((slope & 0x80) && !(slope & 0x10)) ? -1 : 1) * (((*(int8_t*)&slope_signed / 8)) + ((slope == 0x7f || slope == 0xE1) ? 1 : 0)); /*WTF is this*/
}

//get exponent
uint16_t get_exp_rom(AMY* amy, uint16_t val)
{
    return (((uint32_t)amy->base_exp_rom[val >> 3] + (uint32_t)amy->int_exp_rom[(((val / 0x40) & 15) << 3) + (val & 7)] * 0x10) >> 3);
}

//get 1st quarter of sine
uint16_t get_sin_rom_aux(AMY* amy, uint16_t phase)
{
    return ((uint32_t)amy->base_sin_rom[phase >> 4] + ((uint32_t)amy->int_sin_rom[((((0x100 * 0x10 - phase) / 0x100) & 15) << 4) + (phase & 15)] * 0x40)) >> 5;
}

//this one converts 20-bit phase (14 MSBs of it) to sine wave using sine ROMs
//13-bit signed output?
int16_t get_sin_rom(AMY* amy, uint32_t phase)
{
    switch(phase >> (12 + 6)) //two MSBs tell which quarter of sine wave is used
    {
        case 0:
        {
            return get_sin_rom_aux(amy, (phase >> 6)); break;
        }
        case 1:
        {
            //return get_sin_rom_aux(amy, (1 << 11) - (phase >> 8)); break;
            return get_sin_rom_aux(amy, (1 << 13) - (phase >> 6)); break;
        }
        case 2:
        {
            //return -1 * get_sin_rom_aux(amy, (phase >> 8) - (1 << 12)); break;
            return -1 * (int16_t)get_sin_rom_aux(amy, (phase >> 6) & 0xfff); break; //wtf why it works?
        }
        case 3:
        {
            //return -1 * get_sin_rom_aux(amy, (1 << 13) - (phase >> 8)); break;
            return -1 * (int16_t)get_sin_rom_aux(amy, (1 << 14) - ((phase >> 6))); break;
        }
        default: break;
    }

    return 0;
}

void amy_clock(AMY* amy) //one sound sample
{
    for(int i = 0; i < (amy->forty_harm ? 40 : AMY_NUM_HARMONIC_OSCILLATORS); i++)
    {
        amy_harmonic_oscillator* harm_osc = &amy->harm_osc[i];

        if(harm_osc->env.running)
        {
            harm_osc->env.counter++;

            if(harm_osc->env.counter >= AMY_SLOPE_TO_SAMPLE_CYCLES(harm_osc->env.slope)) //destination resolution 1/4 dB, but datasheet says that step resolution is 1/128 dB
            {
                harm_osc->env.counter = 0;

                harm_osc->env.curr_val += my_min(((uint16_t)harm_osc->env.destination << 5) - harm_osc->env.curr_val, amy_slope_to_step_vol(harm_osc->env.slope));

                if(harm_osc->env.curr_val == ((uint16_t)harm_osc->env.destination << 5)) harm_osc->env.running = false;
            }
        }
    }

    uint8_t harm_index = 0;
    amy->output = 0;

    for(int i = 0; i < AMY_NUM_CHANNELS; i++)
    {
        amy_channel* ch = &amy->chan[i];

        if(ch->env.running)
        {
            ch->env.counter++;

            if(ch->env.counter >= AMY_SLOPE_TO_SAMPLE_CYCLES(ch->env.slope)) //destination resolution 1/64th of semitone, but datasheet says that step resolution is 1/2048th of semitone
            {
                ch->env.counter = 0;

                ch->env.curr_val += my_min(((uint32_t)ch->env.destination << 5) - ch->env.curr_val, amy_slope_to_step_pitch(ch->env.slope));

                if(ch->env.curr_val == ((uint32_t)ch->env.destination << 5)) ch->env.running = false;
            }
        }
        
        //TODO: remove direct freq env addition and replace it with a proper EXP ROM calc
        //to convert semitones into frequency for phase acc
        //somewhere here mysterious octave splitter ROM should be
        ch->phase += ch->env.curr_val;
        ch->phase &= ((1 << AMY_ACC_BITS) - 1);

        //TODO: apply their volume envelopes and stuff, and work in INDIVIDUAL mode (time domain multiplexing hell!)

        amy->channel_output[i] = 0;

        //cycle through the harmonics for the current channel
        //1st two harmonics always used by 1st channel
        for(; (!(amy->harmonic_pair_flags & (1 << (harm_index / 2))) && harm_index < (amy->forty_harm ? 40 : AMY_NUM_HARMONIC_OSCILLATORS)) || (i == 0 && harm_index < 2); harm_index++)
        {
            //phase scramble
            //take the harmonic osc index (6 bit), reverse bit order in it
            //and add to the phase as 6 MSBs of it
            //(harm_index << 2) is because we reverse only 6 bits and LUT solution is for 8 bits
            //TODO: remove harmonic multiplier and replace it with a proper EXP ROM calc
            uint32_t final_phase = ch->phase * (harm_index + 1) + ((uint32_t)amy_reverse_byte(harm_index << 2) << 14);
            final_phase &= (1 << 20) - 1;
            
            //todo: return final phase
            amy->channel_output[i] += get_sin_rom(amy, final_phase) / 4;
        }

        amy->output += amy->channel_output[i];
    }

    //TODO: mysterious noise gen!
}

